        if (isset($response['status']) && empty($response['skip'])) {
                        echo "<script>
                  window.addEventListener('message', function (response) {
                  if (response.data === 'loaded') {
                     parent.postMessage(JSON.stringify(" . json_encode($response) . "), response.origin);
                      }
                  }, false);
                </script>";
        }
    }
}